import './App.css';
import PlayButton from './components/PlayButton'
// import AudioElement from './components/AudioElement'
import React from 'react'

export default class App extends React.Component{

  state = {
    playing: false,
    song: process.env.PUBLIC_URL + 'thedeli.mp3',
    audioElement: null,
    audioContext: null,
    gain: null
  }

  componentDidMount(){
    const AudioContext = window.AudioContext || window.webkitAudioContext;
    const audioContext = new AudioContext();
    this.setState({audioContext: audioContext})
    const audioElement = document.querySelector('audio');
    this.setState({audioElement:audioElement})
    const track = audioContext.createMediaElementSource(audioElement);
    track.connect(audioContext.destination);
    const gainNode = audioContext.createGain();
    this.setState({gain: gainNode})
    track.connect(this.state.gan).connect(audioContext.destination);

  }

  playClickHandler = (e) => {
    console.log(e)
    console.log(this.state.gain, "gainnn")
    this.setState({playing: !this.state.playing})
    this.state.audioContext.resume();
    if(this.state.playing === false){
      console.log("playing")
      this.state.audioElement.play()
    }else if(this.state.playing === true){
      console.log("notplaying")
      this.state.audioElement.pause()
    }
  }

  accessAudioElement = (e) => {
  }

  render(){
    return(
      <div> 
      <h1> Tone Handler </h1>
      <audio src={this.state.song}></audio>
      <PlayButton playClickHandler={this.playClickHandler} />

      </div>
    )
  }

}
