import React from 'react'
import MusicNote from './MusicNote'

export default class Graphics extends React.Component{
	state = {
		musicNoteImage: process.env.PUBLIC_URL + 'music-note.png',
		dragging: null,
		dragged: false 
	}

	componentDidMount(){

	}


	drag = (e) => {
		console.log(e.screenY)
		this.setState({drag_value: e})
		this.setState({dragged: !this.state.dragged})
	}



	render(){


		return(

			<div>

				<h1> Graphics </h1>
				<div style={{
					border:"1px solid red",
					width:"200px",
					height: "200px"

				}}> 
					{this.state.dragged ? <MusicNote /> : null}
				</div>
				<MusicNote drag = {this.drag} />
			</div>
		)
	}
}
