    this.setState({playing: e},
    console.log(this.state.playing))
    this.checkToPlaySong()
  }

  checkToPlaySong = () => {
    if(this.state.playing === "false"){
        audioElement.play();
    }
  }

  render(){
    return(
      <div> 
      <h1> Tone Handler </h1>
      <audio src={this.state.song}></audio>
