import React from 'react'

export default class PlayButton extends React.Component{
	state = {
		playing: true
	}
	render(){
		return(
			<button> {this.state.playing ? (this.setState({playing: this.state.playing}), "Pause") : "true"} </button> 
		)
	}
}
