import React from 'react'
import ReactDOM from 'react-dom'

export default class MusicNote extends React.Component{

	state = {
		draggedElementX: null,
		draggedElementY: null
	}
	render(){
	return(
		<div>
			<img style ={{
				  position: "absolute",
				  x: "00",
				  y: "100",
					border: "3px solid #73AD21"

			}}src = {process.env.PUBLIC_URL + 'music-note.png'} draggable = "true" onDrag = {(e) => {
					console.log(e.pageX, e.pageY)
					this.setState({draggedElement:e})
			}}/>
		</div>
		)
	}

}
