import React from 'react'

export default class PlayButton extends React.Component{
	state = {
		playing: true
	}
	render(){
		return(
			<button onClick={() => {
				this.setState({playing: !this.state.playing})
			}}> {this.state.playing ? "Pause" : "Play"} </button> 
		)
	}
}
