import React from 'react'

export default class Effects extends React.Component{

	state = {
		gainLevel: null
	}	

	render(){
		return(
			<div> 
				<h4> effects </h4>
				<p> Volume </p> 
				<input onChange={(e)=> {
					console.log(e.target.value)
					this.setState({gainLevel: e.target.value},

					)
				}} type="range" id="volume" min="0" max="2" value={this.state.gainLevel} step="0.01"/>
			</div>
		)
	}
}
