import React from 'react'
import ReactDOM from 'react-dom'

export default class MusicNote extends React.Component{

	state = {
		draggedElementX: 0,
		draggedElementY: 0,
		elementPosition: 0
	}
	render(){
	return(
		<div>
			<img style ={{
				  position: "absolute",
				  top: this.state.draggedElementX,
				  left: this.state.draggedElementY,
					border: "3px solid #73AD21"

			}}src = {process.env.PUBLIC_URL + 'music-note.png'} draggable = "true" onDrag = {(e) => {
					e.preventDefault()
					console.log(e.pageX, e.pageY)
					this.setState({draggedElementX:e.pageX-40})
					this.setState({draggedElementY:e.pageY-40})
					this.setState({elementPosition: e.style})
			}}/>
		</div>
		)
	}

}
